import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegisterForm = ({onLogin}) => {
  // State to manage user input
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [registerType, setRegisterType] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const [DOB, setDOB] = useState('');
  

  const handleSubmit = async(e) => {
    
    e.preventDefault();
    

    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            // 'Username': username,
            // 'Password': password,
            // 'LoginType': loginType
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in");
        onLogin();
      }
      else if (response.status === 400){
        setLoginStatus("Error: Username or Password incorrect")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div>
        <p>REGISTER</p>


        <div className='inputBox'>
        <label>I am:</label>
        <select onChange={(e) => setRegisterType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="Customer">A Customer</option>
          <option value="Airline Staff">Airline Staff</option>
        </select>
      </div>
    <form onSubmit={handleSubmit} className='flightSearchBox'>
      <div>
        <label htmlFor="firstName">First Name:</label>
        <input
          className='inputBox'
          type="text"
          id="firstName"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="lastName">Last Name:</label>
        <input
          className='inputBox'
          type="text"
          id="lastName"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
        />
      </div>
     
      <div>
      <label htmlFor="dateInput">DOB:</label>
      <input
        className='inputBox'
        type="date"
        id="departDate"
        value={DOB}
        onChange={(e) => setDOB(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>


      

        {registerType === "Customer" && (
            <div>

            </div>
        )}

        {registerType === "Airline Staff"}






    <button type="submit">Login</button>
    </form>
    <div>
      <p>{loginStatus}</p>
    </div>

    </div>
  );
};

export default RegisterForm;
