    //         <p>Previous Year Spending: ${yearSpending}</p>
            
    //           <h2>Last 6 Month Spending Breakdown</h2>
    //         <table style={{ width: '50%', borderCollapse: 'collapse', margin: '20px' }}>
    
    //           <thead>
    //             <tr>
    //               <th>Date Range</th>
    //               <th>Spending</th>
    //             </tr>
    //           </thead>
    //           <tbody>
    //             {displayItems.map(([key, value]) => (
    //               <tr key={key}>
    //                 <td>{key}</td>
    //                 <td>${value}</td>
    //               </tr>
    //             ))}
    //           </tbody>
    //         </table>

    //           <h2>See Spending on Custom Dates</h2>

    //         <form onSubmit={handleCustomDates}>
    //             <DateInputBox setState={setStart} label={"Start of Range"}> </DateInputBox>
    //             <DateInputBox setState={setEnd} label={"Start of Range"}> </DateInputBox>
    //             <button type="submit">Search</button>
    //         </form>

    //       {allowDisplayCustom && (
    //         <table style={{ width: '50%', borderCollapse: 'collapse', margin: '20px' }}>
          
    //           <thead>
    //             <tr>
    //               <th>Date Range</th>
    //               <th>Spending</th>
    //             </tr>
    //           </thead>
    //           <tbody>
    //             {displayItemsCustom.map(([key, value]) => (
    //               <tr key={key}>
