import React, { useState } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function FlightSearch() {
  // State to manage user input
  const [departAirport, setDepartAirport] = useState('');
  const [arriveAirport, setArriveAirport] = useState('');
  const [departDate, setDepartDate] = useState('0000-00-00');
  const [arriveDate, setarriveDate] = useState('0000-00-00');
  

  const handleSubmit = async(e) => {
    e.preventDefault();


    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'DepartAirport': departAirport,
            'arriveAirport': arriveAirport,
            'DepartDate': departDate,
            'ArriveDate': arriveDate
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in") //CHANGE TO DISPLAY
      }
      else if (response.status === 400){
        setLoginStatus("No Flights found")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div className='flightSearchBox'>
    <form onSubmit={handleSubmit} >
      <div>
        <label htmlFor="departAirport">Departure Airport/City:</label>
        <input
          className='inputBox'
          type="text"
          id="departAirport"
          value={departAirport}
          onChange={(e) => setDepartAirport(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="arriveAirport">Arrival Airport/City:</label>
        <input
          className='inputBox'
          type="text"
          id="arriveAirport"
          value={arriveAirport}
          onChange={(e) => setArriveAirport(e.target.value)}
        />
      </div>
      <div className='inputBox'>
        <label >One-Way or Round Trip?</label>
        <select onChange={(e) => setLoginType(e.target.value)}>
          <option value="">Select...</option>
          <option value="One-Way">One-Way</option>
          <option value="Round-Trip">Round-Trip</option>
        </select>
      </div>

      <div>
      <label htmlFor="dateInput">Departure Date :</label>
      <input
        className='inputBox'
        type="date"
        id="departDate"
        value={departDate}
        onChange={(e) => setDepartDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        <div>
      <label htmlFor="dateInput">Return Date :</label>
      <input
        className='inputBox'
        type="date"
        id="arriveDate"
        value={arriveDate}
        onChange={(e) => setarriveDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        {}


      <button type="submit">Search Flight</button>


    </form>
    <div>
      <p>{loginStatus}</p>
    </div>


    </div>
  );
};

export default FlightSearch;
