import React, { useState, useEffect } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function StatusSearch() {
  // State to manage user input
  const [airlineName, setAirlineName] = useState('');
  const [flightNum, setFlightNum] = useState('');
  const [departDate, setDepartDate] = useState('0000-00-00');
  const [status, setStatus] = useState("");
 


  const handleSubmit = async(e) => {
    e.preventDefault();

    const response = await fetch('/StatusSearch', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'AirlineName': airlineName,
            'FlightNum': flightNum,
            'DepartDate': departDate,

        })
      });
      const responseData = await response.json();
      console.log(responseData);
      console.log(responseData['Flights']);

      if (response.status === 200){
        setStatus(responseData['Status'])
        setWay1Flights(responseData["Way1"]); 
        setWay2Flights(responseData["Way2"]);

      }
      else {// (response.status === 400)
        setResult("No Flights Found");
        setWay1Flights(null);
        setWay2Flights(null);
      }
  };


  return (
    <div>
    <form onSubmit={handleSubmit} className='center'>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          className='inputBox'
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          className='inputBox'
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      <div className='inputBox'>
        <label>I am:</label>
        <select onChange={(e) => setLoginType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="Customer">A Customer</option>
          <option value="Airline Staff">Airline Staff</option>
        </select>
      </div>


      <button type="submit">Login</button>


    </form>
    <div>
      <p>{loginStatus}</p>
    </div>


    </div>
  );
}

export default StatusSearch;
