import React from 'react';

const DateInputBox = ({setState, label }) => {
  const handleChange = (event) => {
    setState(event.target.value);
  };

  return (
    <div>
        <label htmlFor="dateInput">{label}</label>
        <input
            className='inputBox'
            type="date"
            onChange={handleChange}
            pattern="\d{4}-\d{2}-\d{2}"
            required
        />
      </div>
  );
};

export default DateInputBox;
