import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const RegisterForm = ({onLogin}) => {
  // State to manage user input
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginType, setLoginType] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  

  const handleSubmit = async(e) => {
    
    e.preventDefault();
    
   
    console.log('Username:', username);
    console.log('Password:', password);

    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'Username': username,
            'Password': password,
            'LoginType': loginType
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in");
        onLogin();
      }
      else if (response.status === 400){
        setLoginStatus("Error: Username or Password incorrect")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div>
        <p>REGISTER</p>


        <div className='inputBox'>
        <label>I am:</label>
        <select onChange={(e) => setLoginType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="Customer">A Customer</option>
          <option value="Airline Staff">Airline Staff</option>
        </select>
      </div>
    <form onSubmit={handleSubmit} className='flightSearchBox'>
      <div>
        <label htmlFor="firstName">First Name:</label>
        <input
          className='inputBox'
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="password">Last Name:</label>
        <input
          className='inputBox'
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      <div className='inputBox'>
        <label>I am:</label>
        <select onChange={(e) => setLoginType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="Customer">A Customer</option>
          <option value="Airline Staff">Airline Staff</option>
        </select>
      </div>


      <button type="submit">Login</button>


    </form>
    <div>
      <p>{loginStatus}</p>
    </div>

    </div>
  );
};

export default RegisterForm;
