import React, { useState } from 'react';
import TextInputBox from './TextInputBox';

const RegisterForm = ({onLogin}) => {
  // State to manage user input
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [registerType, setRegisterType] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const [DOB, setDOB] = useState('');
  const [password, setPassword] = useState('');

  const [username, setUsername] = useState('');
  const [airlineName, setAirlineName] = useState('');
  const [emailAddress, setEmailAddress] = useState('');

  const [buildingNumber, setBuildingNumber] = useState(0);
  const [streetName, setStreetName] = useState('');
  const [apartmentNumber, setApartmentNumber] = useState(0);
  const [city, setCity] = useState('');
  const [stateName, setStateName] = useState('');
  const [zipCode, setZipCode] = useState('');
  const [passportNumber, setPassportNumber] = useState('');
  const [passportExpiration, setPassportExpiration] = useState('');
  const [passportCountry, setPassportCountry] = useState('');

  

  const handleSubmit = async(e) => {
    
    e.preventDefault();
    

    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            // 'Username': username,
            // 'Password': password,
            // 'LoginType': loginType
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in");
        onLogin();
      }
      else if (response.status === 400){
        setLoginStatus("Error: Username or Password incorrect")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div>
        <p>REGISTER</p>
        {/* { value, setState, label } */}
        
    <form onSubmit={handleSubmit} className='flightSearchBox'>
     
    
     
      <div>
      <label htmlFor="dateInput">DOB:</label>
      <input
        className='inputBox'
        type="date"
        id="departDate"
        value={DOB}
        onChange={(e) => setDOB(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        {registerType === "Customer" && (
            <div>

            </div>
        )}

        {registerType === "Airline Staff" && (
            <div>

            </div>
        )}






    <button type="submit">Login</button>
    </form>
    <div>
      <p>{loginStatus}</p>
    </div>

    </div>
  );
};

export default RegisterForm;
