import React from 'react';

const DisplayMap = ({mapToDisplay, clickable=false, setState=null, state=null}) => {
    const handleItemClick = (item) => {
        
        // If onSelect is provided, call it with the selected item or null (to unselect)
        if (setState) {
          setState(state === item ? null : item);
        }
        // console.log(state);
        
      };

    if (clickable){
        // console.log(state)
        return (
            <ul>
              {mapToDisplay.map((item, index) => (
                <li
                key={index}
                onClick={() => handleItemClick(item)}
                style={{
                  cursor: clickable ? 'pointer' : 'default',
                  color: state === item ? 'red' : 'black',
                }}
              >
                  {Object.entries(item).map(([key, value]) => (
                    <div key={key}>
                      <strong>{key}:</strong> {value}<br />
                    </div>
                  ))}
                </li>
              ))}
            </ul>
          );
    }
  return (
    <ul>
      {mapToDisplay.map((item, index) => (
        <li key={index}>
          {Object.entries(item).map(([key, value]) => (
            <div key={key}>
              <strong>{key}:</strong> {value}<br />
            </div>
          ))}
        </li>
      ))}
    </ul>
  );
};

export default DisplayMap;
