import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import DisplayMap from './DisplayMap';


const CustomerHomePage = () => {
  const [displayFlights, setDisplayFlights] = useState(false);
  const [flights, setFlights] = useState('')


  const handleDisplayFlights = async (e) => {
    setDisplayFlights((displayFlights) => !displayFlights );

    if (displayFlights){
      const response = await fetch('/getFlights');

        if (response.ok) {
          const data = await response.json();

          // Update the flights state with the fetched data
          setFlights(data);
        } else {
          // Handle error if the response status is not 200
          console.error('Failed to fetch flights:', response.statusText);
        }
    }
  }


    return (
      <div>
        <div>
          <h1>Hello, Customer</h1>
          <Link to="/FlightSearch">
            <button>Purchase Tickets</button>
          </Link>

          <button onClick={handleDisplayFlights}>See your flights (Toggle)</button>
        </div>

        {displayFlights && (
          <div>
            <p>I wuv you hehe</p>

          </div>
        )}



</div>
      );
};

export default CustomerHomePage
