const NavigationButtons = () => {
    const location = useLocation();
  
    // Conditionally render the button only if not on the CustomerLogin page
    if (location.pathname !== '/CustomerLogin') {
      return (
        <div>
          <Link to="/CustomerLogin">Go to Customer Login</Link>
        </div>
      );
    }
  
    // If on the CustomerLogin page, don't render the button
    return null;
  };

export default NavigationButtons
