import React, { useState, useEffect } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function FlightSearch() {
  // State to manage user input
  const [departAirport, setDepartAirport] = useState('');
  const [arriveAirport, setArriveAirport] = useState('');
  const [departDate, setDepartDate] = useState('0000-00-00');
  const [returnDate, setReturnDate] = useState('0000-00-00');
  const [tripType, setTripType] = useState('');
  const [dbResponse, setDbResponse] = useState(null);
  const [allAirports, setAllAirports] = useState([]);


  const handleSubmit = async(e) => {
    e.preventDefault();

    const response = await fetch('/FlightSearch', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'DepartAirport': departAirport,
            'ArriveAirport': arriveAirport,
            'DepartDate': departDate,
            'ReturnDate': returnDate,
            'TripType' : tripType

        })
      });
      const responseData = await response.json();
      console.log(responseData)
      console.log(responseData.status)
      console.log(responseData.json())
      console.log(responseData['Flights'])

      if (response.status === 200){
        setDbResponse(response["Flights"]) 

      }
      else {// (response.status === 400)
        setDbResponse("No Flights found")
      }
  };

  const fetchData = async () => {
    try {
      const response = await fetch('/getAirports');
      const data = await response.json();
      setAllAirports(data['Airports']);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
    const intervalId = setInterval(() => {
      fetchData();
    }, 99000);

    return () => clearInterval(intervalId);
  }, []); 


  return (
    <div className='flightSearchBox'>
    <form onSubmit={handleSubmit} >


    <div className='inputBox'>
        <label>Select a Departure Airport or Departure City</label>
        <select value={departAirport} onChange={(e) => setDepartAirport(e.target.value)}>
        <option value=""> Select an option</option>
            {allAirports.map(item => (
        <option key={item.Airport_Name || item.City} value={item.Airport_Name || item.City}>
            {item.Airport_Name || item.City}
        </option>
      ))}
      </select>
    </div>

    <div className='inputBox'>
        <label>Select an Arrival Depature Airport or Arrival City</label>
        <select value={arriveAirport} onChange={(e) => setArriveAirport(e.target.value)}>
        <option value="">Select an option</option>
          {allAirports.map(item => (
        <option key={item.Airport_Name || item.City} value={item.Airport_Name || item.City}>
          {item.Airport_Name || item.City}
        </option>
        ))}
</select>
    </div>


      {/* <div>
        <label htmlFor="arriveAirport">Arrival Airport/City:</label>
        <input
          className='inputBox'
          type="text"
          id="arriveAirport"
          value={arriveAirport}
          onChange={(e) => setArriveAirport(e.target.value)}
          required
        />
      </div> */}

      
      <div className='inputBox'>
        <label >One-Way or Round Trip?</label>
        <select onChange={(e) => setTripType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="One-Way">One-Way</option>
          <option value="Round-Trip">Round-Trip</option>
        </select>
      </div>

      <div>
      <label htmlFor="dateInput">Departure Date :</label>
      <input
        className='inputBox'
        type="date"
        id="departDate"
        value={departDate}
        onChange={(e) => setDepartDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        

    {tripType === "Round-Trip" && (
        <div>
      <label htmlFor="dateInput">Return Date :</label>
      <input
        className='inputBox'
        type="date"
        id="returnDate"
        value={returnDate}
        onChange={(e) => setReturnDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required/>
        </div>
    )}
        


      <button type="submit">Search Flight</button>


    </form>
    

    
    <div>
      <h2>Flight List</h2>

      {dbResponse && (
      <ul>
        {dbResponse.map((flight, index) => (
          <li key={index}>
            <strong>Flight Number:</strong> {flight.Flight_Number}<br />
            <strong>Departure Date/Time:</strong> {flight.Departure_Date_Time.toLocaleString()}<br />
            <strong>Arrival Date/Time:</strong> {flight.Arrival_Date_Time.toLocaleString()}<br />
            <strong>Airline:</strong> {flight.Airline_Name}<br />
            <strong>Airplane ID:</strong> {flight.Airplane_ID}<br />
            <strong>Base Price:</strong> {flight.Base_Price}<br />
            <strong>Departure Airport:</strong> {flight.Departure_Airport_Name} ({flight.Departure_City})<br />
            <strong>Arrival Airport:</strong> {flight.Arrival_Airport_Name} ({flight.Arrival_City})<br />
          </li>
        ))}
      </ul>
      )}
    </div>


    </div>
  );
};

export default FlightSearch;
