import React from 'react';

const DisplayMap = ({mapToDisplay, clickable=false, setState=null, state=null}) => {
    const handleItemClick = (item) => {
        
        if (setState) {
          setState(state === item ? null : item);
        }
        
      };

    if (clickable){
        // console.log(state)
        return (
            <ul>
              {mapToDisplay.map((item, index) => (
                <li key={index} onClick={() => handleWay2Click(item)} style={{
                    cursor: 'pointer',
                    color: way2Clicked === item ? 'red' : 'black',
                  }}>
                  {Object.entries(item).map(([key, value]) => (
                    <div key={key}>
                      <strong>{key}:</strong> {value}<br />
                    </div>
                  ))}
                </li>
              ))}
            </ul>
          );
    }
  return (
    <ul>
      {mapToDisplay.map((item, index) => (
        <li key={index}>
          {Object.entries(item).map(([key, value]) => (
            <div key={key}>
              <strong>{key}:</strong> {value}<br />
            </div>
          ))}
        </li>
      ))}
    </ul>
  );
};

export default DisplayMap;
