import React, { useState } from 'react';
import { BrowserRouter as Router, Navigate, Route } from 'react-router-dom';
import LoginForm from './Components/LoginForm';
import Navbar from './Components/Navbar';
import RoutesConfig from './Routes';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [userType, setUserType] = useState('');

  const handleLogin = async () => {
    setLoggedIn(true);

    // Simulated fetch, replace it with your actual logic
    setUserType('userType');
  };

  const handleLogout = () => {
    setLoggedIn(false);
    // Simulated fetch, replace it with your actual logout logic
    // fetch('/logout', {
    //   method: 'POST',
    // });

    // Redirect to the login page
    return <Navigate to="/LoginForm" replace />;
  };

  return (
    <div>
      <Router>
        <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} userType={userType} />
        {/* RoutesConfig should render the appropriate components based on the isLoggedIn state */}
        <RoutesConfig isLoggedIn={isLoggedIn} handleLogin={handleLogin} />

        {/* Define a route for the login form */}
        <Route path="/LoginForm" element={<LoginForm />} />
      </Router>
    </div>
  );
}

export default App;
