import React, { useState } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function FlightSearch() {
  // State to manage user input
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginType, setLoginType] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  

  const handleSubmit = async(e) => {
    e.preventDefault();

   
    console.log('Username:', username);
    console.log('Password:', password);

    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'Username': username,
            'Password': password,
            'LoginType': loginType
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in")
      }
      else if (response.status === 400){
        setLoginStatus("Error: Username or Password incorrect")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div>
    <form onSubmit={handleSubmit} className='flightSearchBox'>
      <div>
        <label htmlFor="username">Departure Airport/City:</label>
        <input
          className='inputBox'
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="password">Arrival Airport/City:</label>
        <input
          className='inputBox'
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className='inputBox'>
        <label>One-</label>
        <select onChange={(e) => setLoginType(e.target.value)}>
          <option value="">Select...</option>
          <option value="Customer">A Customer</option>
          <option value="Airline Staff">Airline Staff</option>
        </select>
      </div>


      <button type="submit">Login</button>


    </form>
    <div>
      <p>{loginStatus}</p>
    </div>


    </div>
  );
};

export default FlightSearch;
