import React from "react";
import { useState, useEffect } from "react";
import DisplayMap from "./DisplayMap";
import DateInputBox from "./DateInputBox";
import TextInputBox from "./TextInputBox";
import Dropdown from "./Dropdown";

// Defaults will be showing all the future flights operated by the airline he/she works for 
// the next 30 days. He/she will be able to see all the current/future/past flights operated by the airline 
// he/she works for based range of dates, source/destination airports/city etc. He/she will be able to see 
// all the customers of a particular flight.

const AirlineStaffHomePage = () => {
    const [futureFlights, setFutureFlights] = useState('');
    const [futureFlightsMessage, setFutureFlightsMessage] = useState('');
    const [futureFlightsCustom, setFutureFlightsCustom] = useState(''); 
    const [futureFlightsCustomMessage, setFutureFlightsCustomMessage] = useState('');

    const [displayFutureFlightsToggle, setDisplayFutureFlightsToggle] = useState('');
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [sourceAirport, setSourceAirport] = useState('');
    const [destinationAirport, setDestinationAirport] = useState(''); 
    const [allAirports, setAllAirports] = useState('');


    
  const fetchData = async () => {
    try {
      const response = await fetch('/getAirports');
      const data = await response.json();
      setAllAirports(data['Airports']);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
    const intervalId = setInterval(() => {
      fetchData();
    }, 99000);

    return () => clearInterval(intervalId);
  }, []); 


    const displayFutureFlights = async(e) => {
        e.preventDefault();
        if (!displayFutureFlightsToggle){
            const response = await fetch('/DefaultFutureFlightsAS', {
                method: 'GET',
                headers:{
                    'Content-Type' : 'application/json'
                },
            });
            if (response.status === 600){
                setFutureFlightsMessage("You are not authorized for this action.");
            }
            const data = await response.json();
            setFutureFlights(data);
            if (data.length === 0){
                setFutureFlightsMessage("No future flights found.");
            }
            else{
                setFutureFlightsMessage("");
            }
        }
        setDisplayFutureFlightsToggle((displayFutureFlightsToggle) => !displayFutureFlightsToggle );
    }

    const handleSubmitCustom = async(e) => {
        e.preventDefault();
        const response = await fetch('/CustomFutureFlightsAS', {
            method: 'POST',
            headers:{
                'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                'start': start,
                'end': end
            })
        });
        if (response.status === 600){
            setFutureFlightsMessage("You are not authorized for this action.");
        }
        const data = await response.json();
        
        console.log(data)
        if (data.length === 0){
            setFutureFlightsCustomMessage("No future flights found in the given range.");
        }  
        else{
            setFutureFlightsCustomMessage("");
        }
        setFutureFlightsCustom(data);
    }











  return (
    <div>
      <h1>Airline Staff Home Page</h1>
        <button onClick={displayFutureFlights}>Display Future Flights </button>


        {displayFutureFlightsToggle && (
            <div>  
                <h1>Future Flights Within 30 Days</h1>
                <p>{futureFlightsMessage}</p>
                <DisplayMap mapToDisplay={futureFlights} clickable={false} />

                <h1>Custom Range for Flights</h1>
            
        <div style={{ display: 'flex', flexDirection: 'row' }}>
            <div style={{ marginLeft: '10px' }}>
                <DateInputBox setState={setStart} label={"Start of Range"} />
                <DateInputBox setState={setEnd} label={"End of Range"} />
                <button onClick={handleSubmitCustom}>Search by Date Range</button>
            </div>

            <div style={{ marginLeft: '10px' }}>
                <TextInputBox setState={setSourceAirport} label={"Source Airport/City"} />
                <button onClick={handleSubmitCustom}>Search by Source Airport/City</button>
            </div>

            <div style={{ marginLeft: '10px' }}>
            <Dropdown items={allAirports} setState={setSourceAirport} label={"Source Airport/City"} />
                <button onClick={handleSubmitCustom}>Search by Destination Airport/City</button>
            </div>

            <div style={{ marginLeft: '10px' }}>
            <Dropdown items={allAirports} setState={setSourceAirport} label={"Source Airport/City"} />
                <Dropdown items={allAirports} setState={setSourceAirport} label={"Source Airport/City"} />
                <button onClick={handleSubmitCustom}>Search by Source/City and Destination Airport/City</button>
            </div>

            <div >
                <Dropdown items={allAirports} setState={setSourceAirport} label={"Source Airport/City"} />
            </div>
        </div>

            {futureFlightsCustom &&(
                <div>
                    <p>{futureFlightsCustomMessage}</p>
                    <DisplayMap mapToDisplay ={futureFlightsCustom}></DisplayMap>

                </div>
            )}

            </div>
            )}
    </div>
  );
}

export default AirlineStaffHomePage;
