import React, { useState, useEffect } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function FlightSearch() {
  // State to manage user input
  const [departAirport, setDepartAirport] = useState('');
  const [arriveAirport, setArriveAirport] = useState('');
  const [departDate, setDepartDate] = useState('0000-00-00');
  const [returnDate, setReturnDate] = useState('0000-00-00');
  const [tripType, setTripType] = useState('');
  const [dbResponse, setDbResponse] = useState('');
  const [allAirports, setAllAirports] = useState([]);


  const handleSubmit = async(e) => {
    e.preventDefault();

    const response = await fetch('/FlightSearch', {
        method: 'GET',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'DepartAirport': departAirport,
            'ArriveAirport': arriveAirport,
            'DepartDate': departDate,
            'ReturnDate': returnDate,
            'TripType' : tripType

        })
      });
      console.log(response)
      if (response.status === 201){
        setDbResponse("Logged in") //CHANGE TO DISPLAY
      }
      else {// (response.status === 400)
        setDbResponse("No Flights found")
      }
  };

  const fetchData = async () => {
    try {
      const response = await fetch('/getAirports');
      const data = await response.json();
      setAllAirports(data['Airports']);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    // Call fetchData when the component mounts
    fetchData();

    // Set up an interval to call fetchData every second
    const intervalId = setInterval(() => {
      fetchData();
    }, 1000);

    return () => clearInterval(intervalId);
  }, []); // The empty dependency array ensures it runs only once, similar to componentDidMount


  return (
    <div className='flightSearchBox'>
    <form onSubmit={handleSubmit} >
    <div>
      <label>Select an option:</label>
      <select value={departAirport} onChange={(e) => setDepartAirport(e.target.value)}>
  <option value="">Select an option</option>
  {allAirports.map(item => (
    <option key={item.Airport_Name || item.City} value={item.Airport_Name || item.City}>
      {item.Airport_Name || item.City}
    </option>
  ))}
</select>
    </div>


    <div>
      <label>Select an option:</label>
      <select value={arriveAirport} onChange={(e) => setDepartAirport(e.target.value)}>
  <option value="">Select an option</option>
  {allAirports.map(item => (
    <option key={item.Airport_Name || item.City} value={item.Airport_Name || item.City}>
      {item.Airport_Name || item.City}
    </option>
  ))}
</select>
    </div>


      <div className='inputBox'>
        <label >One-Way or Round Trip?</label>
        <select onChange={(e) => setTripType(e.target.value)} required>
          <option value="">Select...</option>
          <option value="One-Way">One-Way</option>
          <option value="Round-Trip">Round-Trip</option>
        </select>
      </div>

      <div>
      <label htmlFor="dateInput">Departure Date :</label>
      <input
        className='inputBox'
        type="date"
        id="departDate"
        value={departDate}
        onChange={(e) => setDepartDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        

    {tripType === "Round-Trip" && (
        <div>
      <label htmlFor="dateInput">Return Date :</label>
      <input
        className='inputBox'
        type="date"
        id="returnDate"
        value={returnDate}
        onChange={(e) => setReturnDate(e.target.value)}
        pattern="\d{4}-\d{2}-\d{2}"
        required/>
        </div>
    )}
        


      <button type="submit">Search Flight</button>


    </form>
    <div>
      <p>{dbResponse}</p>
    </div>


    </div>
  );
};

export default FlightSearch;
