import { Link, useLocation } from 'react-router-dom';


const LoginButton = () => {
    const location = useLocation();
  
    // Conditionally render the button only if not on the CustomerLogin page
    if (location.pathname !== '/Login') {
      return (
        <div className=''>
          <Link to="/Login">LOGIN HERE</Link>
        </div>
      );
    }
  
    // If on the CustomerLogin page, don't render the button
    return null;
  };

export default LoginButton
