import React, { useState } from 'react';


// //a. Search for future flights based on source city/airport name, destination city/airport name, 
// departure date for one way (departure and return dates for round trip).



// b. Will be able to see the flights status based on airline name, flight number, arrival/departure 
// date.


function FlightSearch() {
  // State to manage user input
  const [departAirport, setDepartAirport] = useState('');
  const [arrivetAirport, setArrivetAirport] = useState('');
  const [departDate, setDepartDate] = useState('0000-00-00');
  const [arriveDate, setarriveDate] = useState('0000-00-00');
  

  const handleSubmit = async(e) => {
    e.preventDefault();


    const response = await fetch('/CustomerLoginAuth', {
        method: 'POST',
        headers:{
            'Content-Type' : 'application/json'
        },
        body: JSON.stringify({
            'departAirport': departAirport,
            'arrivetAirport': arrivetAirport,
            'departDate': loginType,
            'arriveDate': arriveDate
        })
      });
      console.log(response)
      if (response.status === 201){
        setLoginStatus("Logged in")
      }
      else if (response.status === 400){
        setLoginStatus("Error: Username or Password incorrect")
      }
      else if (response.status === 405){
        setLoginStatus("Please enter values.")
      }


  };

  return (
    <div className='flightSearchBox'>
    <form onSubmit={handleSubmit} >
      <div>
        <label htmlFor="username">Departure Airport/City:</label>
        <input
          className='inputBox'
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="password">Arrival Airport/City:</label>
        <input
          className='inputBox'
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className='inputBox'>
        <label >One-Way or Round Trip?</label>
        <select onChange={(e) => setLoginType(e.target.value)}>
          <option value="">Select...</option>
          <option value="One-Way">One-Way</option>
          <option value="Round-Trip">Round-Trip</option>
        </select>
      </div>

      <div>
      <label htmlFor="dateInput">Departure Date :</label>
      <input
        className='inputBox'
        type="date"
        id="dateInput"
        // value={selectedDate}
        // onChange={handleDateChange}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        <div>
      <label htmlFor="dateInput">Departure Date :</label>
      <input
        className='inputBox'
        type="date"
        id="dateInput"
        // value={selectedDate}
        // onChange={handleDateChange}
        pattern="\d{4}-\d{2}-\d{2}"
        required
      />
        </div>

        {}


      <button type="submit">Search Flight</button>


    </form>
    <div>
      <p>{loginStatus}</p>
    </div>


    </div>
  );
};

export default FlightSearch;
