import React from 'react';

const DisplaySQL = ({mapToDisplay, clickable=false, setState=null, state=null, onChange=null}) => {

    const handleClick = (e,item) => {
      e.preventDefault();
      if (item && setState) {
        setState(item);
      }
  
      if (onChange) {
        onChange();
      }
    };
    

    if (clickable){
        return (
            <ul>
              {mapToDisplay.map((item, index) => (
                <li  key={item} onClick={() => handleClick(item)} style={{
                    cursor: 'pointer',
                    color: state === item ? 'red' : 'black',
                  }}>
                  {Object.entries(item).map(([key, value]) => (
                    <div key={key}>
                      <strong>{key}:</strong> {value}<br />
                    </div>
                  ))}
                </li>
              ))}
            </ul>
          );
    }
  return (
    <ul>
      {mapToDisplay.map((item, index) => (
        <li key={index}>
          {Object.entries(item).map(([key, value]) => (
            <div key={key}>
              <strong>{key}:</strong> {value}<br />
            </div>
          ))}
        </li>
      ))}
    </ul>
  );
};

export default DisplaySQL;
