import React from 'react';

const TextInputBox = ({ value, setState, label }) => {
  const handleChange = (event) => {
    setState(event.target.value);
  };

  return (
    <div>
        <label>{label}</label>
        <input
        type="text"
        value={value}
        onChange={handleChange}
        />
    </div>
  );
};

export default TextInputBox;
